def convert_bytes_to_human_readable(bytes_value):
    units = ["B", "KB", "MB", "GB", "TB"]
    unit_index = 0
    while bytes_value >= 1024 and unit_index < len(units) - 1:
        bytes_value /= 1024.0
        unit_index += 1
    result = "{:.2f} {}".format(bytes_value, units[unit_index])
    return result
